const jwt = require('jsonwebtoken');

/**
 * @param {object} payload - The object to encode
 * @return {object} The encoded JWT
 */
function encodeToken(payload) {
  // ToDo read secret from environment variable or use
  // a certificate
  return jwt.sign(payload, 'secret', {
    expiresIn: '7d',
  });
}

/**
 * @param {object} token - The JWT token to decode
 * @return {object} The decoded token (if it was verified)
 * or an error
 */
function decodeToken(token) {
  try {
    return jwt.verify(token, 'secret');
  } catch (err) {
    throw new Error('Token is invalid');
  }
}

module.exports = {
  encodeToken,
  decodeToken,
};

const { decodeToken } = require('./jwt');
const { User } = require('../models');

const checkAuth = (req, res, next) => {
  // Check if there's a token in the request, if not reject
  const jwt = req.headers.authorization;
  if (!jwt) {
    res.send(401, 'Invalid or missing authorization token');
  }
  // There's a token, see if it's a valid one and retrieve the payload
  else {
    const payload = decodeToken(jwt);
    if (!payload) {
      res.send(401, 'Invalid token');
    } else {
      User.byUsername(payload.username)
        .then(() => next())
        .catch((err) => {
          console.log(err);
        });
    }
  }
};

module.exports = {
  checkAuth,
};
